mapper.update:
UPDATE
	RESER_MANAGE AS r
SET
	r.co_name = #{veCode}
	,r.re_req = NOW()
	,r.re_start = #{reStart}
	,r.re_end = #{reEnd}
WHERE
	r.CODE = #{code}

UPDATE
	RESER_MANAGE
SET
	co_name = #{beCode}
	,re_req = NOW()
	,re_start = #{reStart}
	,re_end = #{reEnd}
WHERE
	code = #{code}
