SELECT s.code ,s.writer_day ,s.sign_end_day ,s.doc_title ,s.doc_name ,d.dename ,p.poname ,e.emp_name AS empName ,s.doc_form_code ,s.sign_state FROM SIG_DOC AS s LEFT JOIN EMP_MANAGE AS e ON s.emp_code = e.emp_num LEFT JOIN DEMG AS d ON e.demgcode = d.CODE LEFT JOIN POSISYS AS p ON e.pocode = p.CODE WHERE s.emp_code = #{sid}
AND s.sign_state = 'Y'
AND s.sign_state = 'N'

SELECT R.code ,R.acc_code ,R.emp_code ,R.doc_form_code ,R.doc_title ,R.doc_name ,DATE_FORMAT(R.re_doc_date,'%Y-%m-%d') AS reDocDate ,R.doc_con ,R.doc_bego ,R.sign_state ,R.sign_check ,DATE_FORMAT(R.writer_day,'%Y-%m-%d') AS writerDay ,R.comments FROM RETURN_DOC_SAVE AS R LEFT JOIN APP_LINE_ACCRUE AS A ON R.acc_code = A.CODE WHERE A.app_person = #{sid}
AND R.sign_state = 'Y'
AND R.sign_state = 'N'
