mapper.select:
SELECT IFNULL(attendance, '미등록') AS attendance, IFNULL(leavework, '미등록') AS leavework FROM ABS_MANAGE WHERE emp_code = #{userCode}
AND annual = DATE_FORMAT(CURDATE(),'%Y%m%d')
AND annual = DATE_FORMAT(#{day},'%Y%m%d')

insert.selectKey:
SELECT code
FROM ABS_MANAGE
WHERE code = #{workAttCode}
